SET SQL_MODE = "NO_AUTO_VALUE_ON_ZERO";
START TRANSACTION;
SET time_zone = "+00:00";

-- tạo bảng category
CREATE TABLE IF NOT EXISTS `category` (
  `cat_id` int(11) NOT NULL,
  `cat_name` varchar(255) COLLATE utf8_unicode_ci NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

-- đổ dữ liệu cho bảng 'category'
INSERT INTO `category` (`cat_id`, `cat_name`) VALUES
(1, 'Trong nước'),
(2, 'Ngoài nước');

-- --------------------------------------------------------

-- tạo bảng commment

CREATE TABLE IF NOT EXISTS `comment` (
  `comm_id` int(11) NOT NULL,
  `prd_id` int(11) NOT NULL,
  `comm_name` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `comm_mail` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `comm_date` datetime NOT NULL,
  `comm_details` text COLLATE utf8_unicode_ci NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

-- Tạo bảng product 
CREATE TABLE IF NOT EXISTS `product` (
  `prd_id` int(11) NOT NULL,
  `cat_id` int(11) NOT NULL,
  `prd_status` int(11) NOT NULL,
  `prd_name` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `prd_image` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `prd_date` datetime NOT NULL,
  `prd_time` int(11) NOT NULL,
  `prd_start` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `prd_price` int(11) UNSIGNED NOT NULL,
  `prd_slot` int(11) NOT NULL,
  `prd_details` text COLLATE utf8_unicode_ci NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

-- đổ dữ liệu cho bảng 'product'
INSERT INTO `product` (`prd_id`, `cat_id`, `prd_status`, `prd_name`, `prd_image`, `prd_date`, `prd_time`, `prd_start`, `prd_price`, `prd_slot`, `prd_details`) VALUES
(1, 1, 0, 'Đà Lạt', 'da_lat.png', '2020-12-12', 3, 'Hà Nội', 2790000, 5, 'Đà Lạt mộng mơ nơi mimosa và ngàn hoa khoe sắc, từ đồi Robin ngắm Hồ Tuyền Lâm, núi Voi, viếng Thiền Viện Trúc Lâm, thăm Dinh Bảo Đại, tản bộ dưới những tán thông, ngắm biệt thự cổ, nhấm nháp ly café ấm áp trong thời tiết se lạnh. Những chuyến xe ngựa thổ mộ chạy quanh Hồ Xuân Hương cũng là nét duyên của Đà Lạt mờ sương.'),
(2, 1, 1, 'Đà Nẵng', 'da_nang.png', '2021-1-2', 4, 'Hồ Chí Minh', 3790000, 4, 'Đà Nẵng nằm giữa ba di sản thế giới: cố đô Huế, phố cổ Hội An và thánh địa Mỹ Sơn. Đà Nẵng còn có nhiều danh thắng tuyệt đẹp say lòng du khách như Ngũ Hành Sơn, Bà Nà, bán đảo Sơn Trà, đèo Hải Vân, sông Hàn thơ mộng và cầu quay Sông Hàn - niềm tự hào của thành phố, và biển Mỹ Khê đẹp nhất hành tinh.'),
(3, 1, 0, 'Nha Trang', 'nha_trang.png', '2020-12-25', 3, 'Đà Nẵng', 4200000, 4, 'Biển Nha Trang tuyệt vời với Vinpearl Nha Trang 5* sang trọng, hòn Mun Hòn Tằm nước trong veo và san hô lộng lẫy, cùng với vịnh Ninh Vân, vịnh Vân Phong hoang sơ và thuần khiết. Viện Hải dương học Nha Trang có trên 20.000 mẫu sinh vật dưới nước, tháp Bà Ponagar hoàn mỹ của người Chăm, cảng Vũng Rô, làng Đại Lãnh và chợ Đầm nhộn nhịp.'),
(4, 1, 0, 'Phú Quốc', 'phu_quoc.png', '2020-12-20', 5, 'Nha Trang', 5790000, 6, 'Phú Quốc là điểm nghỉ dưỡng, tham quan, và khám phá sinh thái tuyệt vời. Mũi Ông Đội, Đá Chào là thế giới san hô và cá biển sặc sỡ. Bãi Sao cát trắng mịn, dáng cong, nước xanh ngọc bích. Đặc sản danh tiếng cả nước là tiêu sọ, nước mắm, rượu sim, ngọc trai. Phú Quốc thực sự là một viên ngọc quý trên bản đồ Việt Nam.'),
(5, 1, 1, 'Vịnh Hạ Long', 'vinh_ha_long.png', '2020-12-15', 2, 'Vũng Tàu', 2000000, 3, 'Vịnh Hạ Long nơi rồng đáp xuống, là danh thắng quốc gia được xếp hạng từ năm 1962. Hạ Long có 1.969 hòn đảo, lô nhô trên mặt biển, nổi tiếng nhất là các hòn Lư Hương, Gà Chọi, Cánh Buồm, Mâm Xôi, đảo Ngọc Vừng, Ti Tốp, Tuần Châu. Hạ Long như bức tranh thủy mặc khổng lồ, tuyệt đẹp, xứng đáng là biểu tượng du lịch Việt Nam.'),
(6, 2, 2, 'Hàn Quốc', 'han_quoc.png', '2021-1-15', 7, 'Hà Nội', 6000000, 8, 'Hàn Quốc xứ kimchi là nơi du lịch với cảnh đẹp và văn hóa tuyệt vời. Seoul sở hữu cung điện Hoàng gia lộng lẫy, chợ Dong Dae Moon, Nam Dae Moon nhộn nhịp, dễ dàng mua nhân sâm, mỹ phẩm và thời trang. Đảo Naomi là bối cảnh phim Hàn Quốc "Bản tình ca mùa đông” và đảo ngọc tình yêu Jeju ngọt ngào cho các đôi uyên ương. Tại Hàn Quốc Bạn có thể thăm quan các điểm đến sau Nami, Jeju, Seoul, Everland, Busan, Gyeongju, Tiger World, Hàn Quốc, Sokcho, Seorak, Suwon, Lotte World, Gangwon, Yongin, Jeonju, Gwangju.'),
(7, 2, 1, 'Mỹ', 'my.png', '2020-12-17', 10, 'Hồ Chí Minh', 8500000, 7, 'Du lịch Mỹ: Hoa Kỳ miền đất hứa ngày nay vẫn là điểm hẹn du lịch mơ ước. Với diện tích rộng lớn từ Bờ Đông sang Bờ Tây, Mỹ được coi là một thế giới thu nhỏ, vô số kỳ quan thiên nhiên, thành phố lịch sử và khu giải trí. Vietravel thường xuyên mở các tuyến du lich Mỹ đến với đến với đất nước đa dạng về bản sắc văn hóa, là trung tâm lớn của cả thế giới, thiên đường du lịch hiện đại bậc nhất này và là địa chỉ thăm thân của người Việt.'),
(8, 2, 2, 'Nhật Bản', 'nhat_ban.png', '2020-12-29', 8, 'Đà Nẵng', 5000000, 8, 'Nhật Bản, xứ sở hoa anh đào, vẫn luôn là nơi thu hút rất nhiều khách du lịch trên thế giới. Đến Nhật Bản, bạn có thể ghé thăm những thành phố hiện đại, sầm uất như Tokyo, Yokohama hay tham quan những vùng đất đậm nét truyền thống, cổ kính như Kyoto, Nagoya; hoặc đắm mình giữa cảnh sắc thiên nhiên xinh đẹp của núi Phú Sĩ và các suối nước nóng ở Noboribetsu… Bên cạnh đó, bạn còn được thưởng thức những món ăn ngon, độc đáo của đất nước này và tham gia các lễ hội văn hóa đậm đà bản sắc dân tộc nơi đây.'),
(9, 2, 2,  'Pháp', 'phap.png', '2021-1-15', 9, 'Vũng Tàu', 7000000, 7, 'Pháp luôn được xem là đất nước lãng mạn nhất thế giới, một quốc gia giàu truyền thống văn hóa, lịch sử. Không chỉ thu hút với nhiều công trình kiến trúc đặc sắc như tháp Eiffel, Nhà thờ Đức Bà Paris cổ kính, cung điện Versailles,… Pháp còn quyến rũ bởi phong cảnh thiên nhiên thơ mộng với những cánh đồng oải hương tuyệt đẹp hay dòng sông Seine yên bình, lãng mạn. Bên cạnh đó, nền ẩm thực Pháp, được xếp vào hàng bậc nhất thế giới, cũng tạo nên sức hút khó cưỡng cho du khách đến đây.'),
(10, 1, 1, 'Huế', 'hue.png', '2021-1-15', 3, 'Cần Thơ', 2500000, 10, 'Mang nét trầm tư và sâu lắng cái hồn của Huế mang một vẻ đẹp thật khó tả. Huế với một vẻ đẹp bình dị mà cổ kính. Sự hữu tình của Huế thì không ai không biết nếu có dịp đi du lịch Huế chỉ cần đi một vòng Huế là đã cảm nhận được cái Hồn của Huế từ cảnh sắc tiếng chuông chùa Thiên Mụ đến những cội cây già bao mùa rêu phủ từ những đứa trẻ thơ ngây đến những cụ già chống gậy dạo những buổi chiều, cảnh sắc nơi đây khiến con người cũng trở nên êm dịu. Hành trình tour du lịch Huế hấp dẫn từ Lữ Hành. Chúng tôi sẽ đưa bạn đến với nhiều danh lam thắng cảnh nét đẹp văn hóa, lịch sử, những nét đẹp cổ kính như: Đại Nội, Điện Thái Hòa, Tử Cấm Thành, Lăng Tự Đức, Ngọ Môn, Thế Miếu, Hiển Lâm Các, Cửu Đỉnh xưa của 13 vị vua triều Nguyễn. Chùa Thiên Mụ lưu giữ cổ vật lịch sử, nghệ thuật quý giá...'),
(11, 1, 2, 'Côn Sơn', 'con_son.png', '2021-1-1', 3, 'Nha Trang', 6000000, 10, 'Tham quan và tìm hiểu một thời quá khứ hào hùng của Dân tộc trong 2 cuộc kháng chiến chống Pháp và chống Mỹ tại địa ngục trần gian Côn Sơn. Không thể thiếu trong tour Côn Đảo là hành trình đến với thiên nhiên biển đảo với những bãi biển tuyệt đẹp của Côn Đảo đem lại giây phút đáng nhớ nhất cho kỳ nghỉ.'),
(12, 1, 1, 'Sa Pa', 'sa_pa.png', '2021-1-12', 2, 'Huế', 2000000, 13, 'Bầu trời trong xanh, không khí trong lành, Sapa rộn ràng với những chàng trai, cô gái dân tộc với nụ cười rạng rỡ mang đến cho du khách một không gian ấm áp không thể nào quên. Đến với Sapa, du khách có dịp được thăm bản Cát Cát để tìm hiểu cuộc sống của đồng bào H’Mông, thưởng thức những đặc sản như cơm lam ủ chín trong ống tre tươi, nhâm nhi thịt lợn nướng với rượu táo mèo, hoà với sắc màu của hoa, của lá, của những hoa văn trên tà áo thổ cẩm của các cô gái H’Mông, Dao, Thái và lời hát tỏ tình du dương theo giai điệu của tiếng sáo, tiếng khèn… càng thêm say lòng người.'),
(13, 2, 2, 'Anh', 'anh.png', '2021-2-1', 5, 'Vũng Tàu', 51000000, 5, 'Du lịch Anh - Vương quốc Anh – Nơi được mệnh danh là "xứ sở sương mù" với đời sống văn hóa và di sản nghệ thuật hết sức phong phú và đa dạng. Vương quốc Anh có khí hậu ôn hòa quanh năm mát mẻ, có lạnh tuyết rơi vào mùa đông và có mưa nhiều vào mùa Hè. Bởi có thời tiết thuận lợi nên Anh là một quốc gia có tốc độ tăng trưởng về du lịch cao. Khi tham gia Tour Du lịch Anh du khách sẽ có dịp khám phá vẻ đẹp trầm mặc cổ kính xen lẫn hiện đại của thành phố London, cùng nhiều nét văn hóa độc đáo đặc trưng của vương quốc còn lưu giữ được từ thời cổ đại.'),
(14, 2, 1, 'Italia', 'italia.png', '2020-12-18', 7, 'Đà Nẵng', 60000000, 7, 'Du lịch Ý - Ý hay còn có tên gọi khác là Italia - Nổi tiếng thế giới với nhiều danh họa nổi tiếng, những công trình kiến trúc độc đáo, là "kinh đô" thời trang thế giới. Ý là một trong những địa điểm du lịch đáng đến nhất nhì Châu Âu. Trong Tour du lịch Ý (Italia) du khách sẽ được tham quan khu di tích Đấu trường La Mã Colosseum, Tháp nghiêng Pasi, và đắm mình vào màn nước trong xanh tại bãi biển Amalfi,... Tour Ý luôn mang đến cho du khách những trải nghiệm khám phá tuyệt vời, không những đến từ cảnh quan thiên nhiên, các công trình kiến trúc cổ kính vĩ đại mà còn đến từ nét lãng mạn, nên thơ của các thành phố như: Venice, Rome, Milan...'),
(15, 1, 2, 'Phong Nha- Kẻ Bàng', 'phong_nha.png', '2021-5-20', 3, 'Hồ Chí Minh', 3000000, 10, 'Du lịch Phong Nha Kẻ Bàng là sự lựa chọn hàng đầu của du khách khi đến Quảng Bình. Phong Nha được ví như “chốn bồng lai tiên cảnh” hay “hoàng cung trong lòng đất”. Với khung cảnh thiên nhiên hoang sơ. Đẹp như một bức tranh thủy mặc mà tạo hóa đã ban tặng cho người dân Quảng Bình. Chính vì vậy Phong Nha kẻ Bàng trở thành điểm du lịch của Quảng Bình hấp dẫn du khách trong và ngoài nước');
-- ----------------------------


-- Cẩm nang
CREATE TABLE IF NOT EXISTS `posts` (
  `post_id` int(11) NOT NULL,
  `post_name` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `post_img` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `post_details` text COLLATE utf8_unicode_ci NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci; 

-- đổ dữ liệu cho bảng 'posts'
--
INSERT INTO `posts` (`post_id`, `post_name`, `post_img`, `post_details`) VALUES 
(1, 'Cuối năm tham gia tour du lịch chùa Hương làm lễ tạ ơn', 'chua_huong.jpeg', 'Rất nhiều du khách khi tham gia, trải nghiệm tour du lịch chùa Hương đầu năm không chỉ là du ngoạn sơn thủy mà còn để vãn cảnh, cầu may, xin lộc. Và rồi vào những ngày cuối năm, một số người lại quay về đây để làm lễ tạ ơn. Vào mùa này, lượng khách đến đây không quá đông, vừa hay để thưởng lãm cảnh đẹp nơi này.Trải nghiệm tour du lịch chùa Hương vào những ngày cuối năm, khi cái rét bắt đầu ùa về, khung cảnh nơi đây cũng im ắng và tĩnh lặng hơn vì lượng khách đổ về đây không nhiều như dịp đầu năm nên rất thuận lợi để thăm thú và "săn" ảnh đẹp. Ý nghĩa của lễ tạ ơn cuối năm.Theo tín ngưỡng tâm linh của người Việt, người ta tin rằng luôn có nhân quả tuần hoàn, có vay thì ắt phải có trả. Và đầu năm đã đi đền, chùa "vay lễ", xin lộc, cầu may thì cuối năm nên về nơi đó làm lễ tạ nhằm tạ ơn chư vị đã bảo bọc, phù hộ cho một năm ấy. Đi trả lễ cho tâm hồn thanh thản, yên tâm bước sang năm mới với nhiều điều may mắn hơn. Điều này không chỉ là tín ngưỡng dân gian mà còn thể hiện truyền thống uống nước nhớ nguồn của người dân đất Việt. Cuối năm cũng là thời điểm hoa súng Suối Yến nở rộ tuyệt đẹp.Mùa đông gõ cửa, dòng Suối Yến khoác lên mình tấm áo mới với sắc hồng tươi đẹp. Du lịch chùa Hương tháng 12, đi dọc theo Suối Yến dài 4km bạn sẽ được chiêm ngưỡng hàng ngàn bông hoa súng căng tràn nhựa sống, vươn mình mạnh mẽ trên mặt nước nhuộm tím cả lòng sông vô cùng lãng mạn. Hoa súng hoa súng thường sẽ nở vào sáng sớm khi sương còn giăng kín cả núi non, và khép bầu hoa lại khi nắng trưa bắt đầu gay gắt. Thế nên, nếu muốn thưởng hoa hay chụp ảnh thì bạn nên đi thật sớm, thời gian đẹp nhất là tầm từ 6 đến 8 giờ sáng. Đi tour du lịch chùa Hương cuối năm là cơ hội tốt nhất để cảm nhận được hết nét đẹp của quần thể danh thắng Hương Sơn.Vì mùa này, lượng khách du lịch về đây không nhiều sẽ là cơ hội tốt để bạn chiêm ngưỡng được hết vẻ đẹp thiên nhiên hùng vĩ, tráng lệ của quần thể chùa quanh triền núi Hương Sơn một cách trọn vẹn nhất với các tuyến: + Tuyến Hương Tích: Đền Trình – Động Tiên Sơn – Chùa Thiên Trù – Đền Trần Song – Chùa Giải Oan –Động Hương Tích – Chùa Hinh Bồng. + Tuyến Thanh Sơn: Chùa Thanh Sơn – Động Hương Đài. + Tuyến Long Vân: Hang Sũng Sàm - Chùa Long Vân – Động Long Vân. + Tuyến Tuyết Sơn: Chùa Bảo Đài – Động Tuyết Sơn – Động Chùa Cá. Thưởng thức những món ăn ngon.Khi đi tour du lịch chùa Hương làm lễ tạ ơn, bạn còn có cơ hội được thưởng thức những món ăn, đặc sản vô cùng hấp dẫn ở nơi đây như: vịt cỏ Vân Đình nướng, chè củ mài, chè lam, bánh củ mài, hạt dẻ, rau sắn,… Một số quán ăn, nhà hàng nổi tiếng ở khu vực chùa Hương bạn có thể tham khảo: + Nhà hàng cơm chay Thủy Tiên - Địa chỉ: Khu Nhà khách chùa Tiên Sơn, chùa Hương. + Nhà hàng cơm chay Tùng Lâm - Địa chỉ: Khu du lịch chùa Hương. + Nhà hàng Quyết Thắng - Địa chỉ: Số 1 khu vực sân chùa Thiên Trù, chùa Hương. + Nhà hàng Thành Ba - Địa chỉ: Số 10 khu vực bến đò Thiên Trù.'),
(2, 'Du lịch Đồng Tháp và khám phá những cái nhất ở nơi này', 'dong_thap.jpg', 'Đồng Tháp là một tỉnh miền Tây Nam Bộ có rất nhiều điểm đến thú vị với cảnh quan thiên nhiên tuyệt đẹp, những kiến trúc cổ kính đầy cuốn hút và những cái nhất gắn liền với các địa danh nổi tiếng ở đây. Du lịch Đồng Tháp bạn nhất định phải khám phá cho bằng hết các địa điểm này nhé.Nơi có cặp Chùa Ông – Chùa Bà nằm giữa lòng thành phố đẹp nhất.Chùa Bà (Thất Phủ Thiên Hậu Cung hay Thiên Hậu Miếu) và chùa Ông (Kiến An Cung, chùa Ông Quách) là những ngôi chùa có lối kiến trúc đẹp, mang màu sắc mang đậm phong cách văn hóa Trung Hoa truyền thống. Đây là một cặp "chùa Ông chùa Bà" duy nhất tọa lạc giữa trung tâm Sa Đéc. + Chùa Bà.Thất Phủ Thiên Hậu Cung có bố cục theo kiểu chữ Đinh - 丁, trước sau liền nhau, không có sân thiên tỉnh. Mái lợp ngói âm dương tạo gợn sóng, trên mái nóc trang trí hình lưỡng long tranh châu cùng tượng các vị tiên, Phật trong truyện xưa tích cũ của Trung Hoa. Nét độc đáo nhất trong chùa Bà là những cây hương vòng to như chiếc nón với tờ sớ được cài ở giữa. Người ta tin rằng, khi thắp lên, tâm nguyện của người thắp như nhờ khói hương đem lời khấn nguyện bay xa lên cõi Niết Bàn. + Chùa Ông.Chùa Ông mang đậm nét kiến trúc Trung Quốc, với tổng thể là hình chữ Công - 工 gồm ba gian: Đông lang, Tây lang và khu chính điện thì rộng hơn. Mái ngói gồm 3 lớp, lợp theo gợn sóng rồng, trải nền cho những ngọn sóng cong vút lên cao theo kiểu "ngũ hành". Chùa không chỉ thờ cúng các tượng Phật mà còn đạo giáo, những vị anh hùng Trung Hoa trước đây,… Huỳnh Thuỷ Lê – Ngôi nhà cổ có lối kiến trúc Trung Hoa xa hoa và cầu kỳ nhất Đồng Tháp.Nằm bên bờ sông Tiền thơ mộng, nhà cổ Huỳnh Thủy Lê được xếp hạng Di tích Quốc gia vào năm 2009. Ngôi nhà này gắn liền với câu chuyện tình của của chàng công tử Huỳnh Thủy Lê người Việt gốc Hoa và nữ văn sĩ người Pháp Margueritte Duras được ghi lại trong cuốn tiểu thuyết "Người tình" nổi tiếng, và dựng thành phim lấy bối cảnh tại chính nơi này. Nhà cổ Huỳnh Thủy Lê được thiết kế theo lối kiến trúc Đông – Tây kết hợp. Nhìn từ xa, ngôi nhà gây ấn tượng trong phong cách Pháp với những bức phù điêu, tượng đắp nổi cùng những ô cửa sổ làm bằng kính đầy sắc màu mang vẻ uy nghiêm và bề thế. Bên trong được trang trí đậm chất Á Đông, nổi bật là bàn thờ Quan Công ngay giữa gian chính theo tín ngưỡng của người Hoa. Là một trong những nơi có lò gạch cổ đẹp nhất miền Tây. Nằm ngay cạnh dòng Sa Giang, những lò gạch cổ ở thành phố Sa Đéc nổi bật nhờ sắc đỏ và cấu tạo đặc biệt trông như những Kim Tự Tháp của Ai Cập. Lò gạch này đã tồn tại hơn 100 năm qua. Dưới ánh nắng vàng rực trong những ngày đẹp trời, các lò gạch hiện ra như những tòa lâu đài nhỏ nhắn nhuốm màu cổ tích. Các mái vòm cong cong in lên nền trời xanh biếc, những dáng hình mềm mại rất cổ, rất duyên. Chùa Phước Kiểng – Ngôi chùa duy nhất có những lá sen khổng lồ từ rừng Amazon. Sen Đồng Tháp vốn dĩ đã nổi tiếng khắp đất nước với sen trắng, sen hồng nở từ rất sớm. Thế nhưng, ngay tại chùa Phước Kiểng (chùa Lá Sen) còn nổi tiếng hơn hẳn với những lá sen khổng lồ có đường kính từ 1.5m - 2m. Lá sen ở đây rất dày, vững chãi có thể chịu được sức nặng lên đến 70kg. Với một tấm phủ tránh cho lá khỏi vỡ, du lịch Đồng Tháp đến đây, bạn có thể đứng trên lá sen để chụp ảnh. Làng hoa Sa Đéc, làng hoa đẹp và lớn nhất Đồng bằng sông Cửu Long. Làng hoa Sa Đéc khởi nguyên là Làng hoa Tân Quy Đông, một làng nghề truyền thống hơn 100 năm tuổi nằm bên bờ sông Tiền quanh năm lộng gió, màu mỡ phù sa, ngập tràn ánh nắng. Làng hoa Sa Đéc được mệnh danh là thủ phủ hoa của miền Tây, nơi ươm mầm khoảng hơn 2.000 loài hoa khác nhau trên diện tích khoảng 500ha, với hơn 2.300 hộ dân trồng hoa. Vì đặc trưng địa lý vùng có địa thế thấp, hay ngập úng. Nên người dân Sa Đéc trồng hoa trên giàn cao, phía dưới đôi khi bị ngập nước hết. Họ phải đi ủng hoặc những tháng mưa phải chèo ghe để chăm sóc hoa. Chính điều đó tạo nên những hình ảnh làng hoa tuyệt vời trong mắt du khách. Vào dịp giáp Tết, đến đây bạn sẽ được chiêm ngưỡng cảnh tượng ngoạn mục với muôn vàn loài hoa khoe sắc, tỏa ngát hương thơm như đã từng thấy trên TV, ảnh lịch,…'),
(3, 'Chơi gì ở Nha Trang? Top 5 điểm đến không nên bỏ lỡ', 'nha_trang.jpg', 'Chụp ảnh ở cầu sắt. Cầu sắt bắt ngang sông Cái, đoạn gần khu tắm bùn Thác Bà là địa điểm chụp ảnh yêu thích của nhiều bạn trẻ du lịch Nha Trang. Được xây dựng từ thời Pháp thuộc nên cầu sắt Nha Trang mang chất hoài cổ. Đường ray ở giữa mang màu củ kỹ khi lên ảnh vô cùng đẹp, đậm chất retro. Tuy nhiên, chụp ảnh ở làn giữa khá nguy hiểm, nên bạn có thể lựa chọn chụp ở làn đường dành cho xe chạy phía bên trái. Vì hiện tại, rất ít xe chạy bên làn đường này mà hầu hết đều tập trung ở làn bên phải, nên bạn có thể tha hồ sống ảo tại đây. Thư giãn ở hòn Chồng. Hòn Chồng là môt bãi đã có rất nhiều những hòn đá lớn nhỏ chồng lên nhau. Không ai biết hòn Chồng có từ bao giờ chỉ biết nơi đây có cảnh sắc tuyệt đẹp, yên ả như tác biệt với sự ồn ào, náo nhiệt. Những làn nước biển xanh biếc đánh vào những khối đã vững chắc như một bức tranh thiên nhiên kỳ ảo đẹp vô cùng. Du lịch Nha Trang đến với hòn Chồng, bạn có thể ngồi trên mõm đá buông câu hoặc di chuyển vài chục mét vào phía bờ để tắm trên bãi cát mịn màng. Lạc bước trên con đường xuyên biển ở đảo Điệp Sơn. Điệp Sơn là một trong những hòn đảo tự nhiên đẹp nhất ở Nha Trang. Địa điểm này ây ấn tượng cho nhiều người nhất không phải vẻ đẹp hoang sơ mà là con đường xuyên biển vô cùng độc đáo. Con đường ở dưới nước tại đảo Điệp Sơn chỉ rộng khoảng 1m và dài chừng 700m, uốn lượn nối liền hòn đảo giữa với đảo Điệp Sơn Lớn. Trải nghiệm đi bộ trên còn đường này sẽ mang lại cho bạn cảm giác vô cùng thích thú, bạn có thể thỏa thích ngắm nhìn những đàn cá nhỏ tung tăng bơi lội hai bên, chốc chốc lại thấy những đàn cá nhảy tung lên khỏi mặt nước như đang giơ tay vẫy chào con thuyền đang chạy lướt qua. Vui chơi ở đồng cừu Suối Tiên. Đồng cừu Suối Tiên là địa điểm du lịch Nha Trang "hot hit" nhất nhì ở Nha Trang. Đây là đồng cừu đầu tiên tại Cam Ranh, có số lượng lên đến hàng trăm con. Khám phá đồng cừu Suối Tiên bạn sẽ được lạc vào thế giới cổ tích giữa trời Âu với nông trại xanh mướt màu cỏ và những cừu trắng muốt đáng yêu, được tự tay bồng bế và vuốt ve, âu yếm, vui chơi cùng chúng. Để thoả mãn "tâm hồn sống ảo" của giới trẻ, đồng cừu Suối Tiên còn có thêm nhiều tiểu cảnh xinh xắn như xích đu, cầu nhỏ, tháp cừu tone màu trắng thuần khiết kết hợp cùng các bậc thang xoáy ấn tượng khiến những tấm hình của bạn trở nên độc đáo và "Tây" hơn. Khám phá tháp Bà Ponagar. Tháp Bà Ponagar là công trình kiến trúc cổ kính hoàn mỹ của người Chăm. Công trình này đã tồn tại trên 10 thế kỷ. Quần thể di tích tháp Bà Ponagar được chia thành 3 tầng, với 4 tòa tháp lớn. Ở tầng thấp nhất là ngôi tháp cổng nằm ngang dưới mặt đất, tuy nhiên kiến trúc này đã bị tàn phá theo thời gian và hiện chỉ còn là phế tích. Tầng giữa gọi là Mandapa dành cho khách hành hương nghỉ ngơi, chuẩn bị lễ vật. Tầng trên cùng bao gồm 2 dãy và 4 tòa tháp được xây dựng bằng gạch nung cứng màu vàng. Trong đó, tòa tháp chính thờ nữ thần Ponagar và phần trên đỉnh tháp thờ thần Shiva cưỡi ngưu thần Nandi cùng những tượng linh vật khác. Ở tháp Bà Nha Trang hiện vẫn còn lưu giữ những bia ký lâu đời của người Chăm được đặt ở tầng trên cùng của tòa tháp chính. Bia ký là dấu tích có giá trị cho những công trình nghiên cứu lịch sử về Tháp Bà Ponagar và hiện nay vẫn chưa được giải mã hoàn toàn. Du lịch Nha Trang đến tháp Bà Ponagar bạn còn được thưởng thức những điệu múa Chăm truyền thống của các nghệ nhân tại đây.'),
(4, 'Gọi tên 5 địa điểm đón Giáng sinh cuốn hút nhất trong nước', 'giang_sinh.jpg', 'Hà Nội. Tháng 12 vừa chạm ngõ, khắp phố phường, ngõ hẻm ở Hà Nội đã được trang hoàng rất lộng lẫy bằng các đồ vật quen thuộc cho Noel với sắc xanh, sắc đỏ lấp lánh vàng với đủ hình dạng. Từ nữa cuối tháng 12 trở đi, Hà Hội dần trở nên lạnh hơn đậm chất mùa đông. Du lịch Hà Nội đón Giáng sinh, hầu hết mọi du khách đều thích đến Nhà thờ Lớn. Vào dịp Noel, nơi đây lại được trang trí vô cùng đẹp, thắp sáng lung linh làm bừng sáng cả khu phố. Ngoài địa điểm đón Giáng sinh lung linh là Nhà thờ Lớn, Noel ở Hà Nội còn thu hút nhiều du khách với những cái tên quen thuộc như Phố Hàng Mã, Vincom Bà Triệu, Royal City, Vincom Nguyễn Chí Thanh, Phố đi bộ và cả các quán cà phê,… đều thay áo mới với hình ảnh cây thông, Ông già Noel, người tuyết, trái châu nhỏ xinh, vòng tòn nguyệt quế, ánh đèn màu nhấp nháy rực rỡ,… Sa Pa. Sa Pa là địa điểm đón Giáng sinh hấp dẫn nếu bạn muốn tạm lánh xa chốn nhộn nhịp, xô bồ để tận hưởng không gian lãng mạn, yên bình bên cạnh người thân. Cuối năm, thị trấn Sa Pa giống như một châu Âu thu nhỏ khi được bao phủ bởi mây mù, đôi lúc lại có tuyết rơi. Dạo quanh các con phố nhỏ ở thị trấn Sa Pa, bạn sẽ dễ dàng bắt gặp những cây thông Noel khổng lồ được trang trí từ rất sớm cùng với tuần lộc và xe trượt tuyết. Khi tuyết rơi, mọi người sẽ gom tuyết lại đắp thành hình người tuyết rất đáng yêu. Nơi được trang trí đẹp nhất và lấp lánh nhất vào mùa Giáng sinh ở đây chính là Nhà thờ Đá. Ghé thăm nơi này bạn không chỉ được chụp ảnh cùng những tiểu cảnh đẹp mê li mà còn được được tham gia lễ cầu nguyện, hát thánh ca cùng người dân xứ đạo. Ngoài ra, bạn cũng có thể ghé các quán cà phê, nhà hàng, trạm ga tàu leo Fansipan như Mường Hoa, Hoàng Liên,... để check-in không khí Giáng sinh thơ mộng, lãng mạn ở đây. Sài Gòn. Có lẽ không ở đâu không khí Giáng sinh lại về sớm như Sài Gòn. Từ những ngày đầu tháng 12, mọi nẻo đường, nhà hàng, quán cà phê, các trung tâm thương mại,… đều được bày biện, tranh trí bằng các món đồ đặc trưng của Noel. Các shop, cửa hàng tưng bừng khuyến mãi và rộn ràng những khúc ca mừng Giáng sinh. Không khí mùa lễ ở Sài Gòn cũng không khác gì ở Hà Nồi Nội nhiều, chỉ khác mỗi chổ khí hậu. Nếu Hà Nội bước vào mùa rét thì Sài Gòn chỉ se se lạnh, rất lý tưởng để dạo chơi. Du lịch Sài Gòn mùa Giáng sinh, bạn hãy cùng hòa vào dòng người để tới các địa điểm trung tâm như Nhà thờ Đức Bà, Nhà thờ Tân Định, Diamond Plaza, Tòa nhà Bitexco,… hoặc đến những khu xóm đạo, phố người Hoa, phố đi bộ, khu đô thị Phú Mỹ Hưng và cầu Ánh Sao. Đây đều là những địa điểm đón Giáng sinh rất được yêu thích ở Sài Gòn. Đà Lạt. Nếu không chịu được thời tiết rét đậm của miền Bắc, lại thích hưởng cái lạnh lạnh hơn Sài Gòn bạn có thể lên đường đến với thành phố Đà Lạt. Thời tiết Đà Lạt không quá lạnh đến mức ai cũng muốn trùm chăn nằm nhà và đủ lạnh để diện các loại trang phục đậm chất Giáng sinh như giày bốt cổ lông, khăn quàng cổ, mũ trùm đầu,… để đi dạo phố. Đặc biệt, đón Giáng sinh ở Đà Lạt bạn sẽ không phải lo ngại tình trạng tắc đường, kẹt xe như ở các thành phố lớn. Nhà thờ Cam Ly, nhà thờ Domaine, nhà thờ Con Gà... là những địa điểm check-in hấp dẫn ở Đà Lạt mà bất kỳ du khách nào đến đây cũng muốn ghé qua Và nếu bạn có người yêu đi cùng, hãy thử cùng đèo nhau trên chiếc xe đạp đôi dạo quanh hồ Xuân Hương, rồi ghé vào một quán cà phê lãng mạn với đèn vàng và nến ấm áp. Hoặc lang thang dạo phố đến nửa đêm, rồi vào lề đường thưởng thức cốc sữa đậu nành, bánh tráng nướng nóng bỏng môi trong cái lạnh se sắt trong đêm Giáng sinh đều rất thú vị. Nam Định. Nam Định nổi tiếng là cái nôi của những giáo đường cổ kính và xứ đạo lớn. Nơi đây cũng là một trong những địa điểm đón Giáng sinh sôi động bậc nhất miền Bắc. Nếu bạn là người con của Chúa, thích tận hưởng đêm Noel an lành trong những tòa nhà thờ lộng lẫy thì Nam Định quả đúng là địa điểm đón Giáng sinh nhất định không thể bỏ qua dành cho bạn đấy. Nơi đây có rất nhiều giáo đường nổi tiếng với kiến trúc đẹp mắt như nhà thờ chính tòa Bùi Chu được xây dựng theo kiến trúc cổ điển với gam màu thổ hoàng, mái vòm hình ô van; đền Thánh Hưng Nghĩa tựa lâu đài cổ kính; Vương cung thánh đường Phú Nhai nổi bật với lối kiến trúc Gothic cổ điển và nét trang trí mang đậm màu sắc châu Âu,… tất cả đều được trang hoàng cực kỳ lộng lẫy trong mùa Giáng sinh.'),
(5, 'Những điểm check-in Ninh Thuận tuyệt đẹp dành cho các cặp đôi', 'ninh_thuan.jpg', 'Với những cánh đồng cỏ cháy, những con đường đất đầy bụi, những đàn dê thả rông,.. du lịch Ninh Thuận "lang thang theo dấu chân cừu" đến những điểm check-in này, các bạn sẽ có một bộ ảnh đẹp đến nỗi khiến người khác phải ganh tị luôn đấy. Tháp Po Klong Garai. Tháp Chăm vốn là một trong những đặc trưng của các tỉnh miền Duyên hải, đó là dấu tích xưa cũ của dân tộc Chăm, sinh sống ở vùng Nam Trung Bộ. Đến tháp Po Klong Garai Ninh Thuận, các bạn không những được chiêm ngưỡng nét kiến trúc Chăm độc đáo mà còn có thể cho ra đời những tấm ảnh điệu nghệ. Được làm từ gạch nung đỏ sẫm, các bức ảnh được chụp ở di tích tháp Po Klong Garai mang sắc màu hoài cổ khiến cho người xem có cảm giác bí ẩn đầy huyền thoại vô cùng cuốn hút. Đồng cừu An Hòa. Du lịch Ninh Thuận đến với Đồng cừu An Hòa các cặp đôi sẽ được trải nghiệm cuộc sống của dân du mục trong không gian yên bình, thơ mộng đậm chất thôn tình. Trên một vùng đồng cỏ thảo nguyên rộng lớn là hàng nghìn chú cừu được chăn thả tự do. Tại đây, các bạn sẽ được chụp ảnh với những chú cừu dễ thương. Trong khuôn viên cánh đồng còn có một dòng suối nhỏ uốn lượn, xung quanh là nhiều hoa lá và những tiểu cảnh xinh xắn như: xích đu, nhà ở, cối xay gió, cầu gỗ với tông màu nhẹ nhàng. Khu du lịch Tanyoli. Khu du lịch Tanyoli một địa điểm du lịch Ninh Thuận lý tưởng cho những fan thích cắm trại và trải nghiệm mạo hiểm. Khu này như một làng Mông Cổ thu nhỏ với lều trại mang dáng dấp thảo nguyên. Bãi đất trống đầy nắng với cỏ cháy và bốn bề núi non, đất đá của xứ này được sắp đặt theo phong cách du mục không thể nào phù hợp hơn được nữa. Thế nên chắc chắn các bạn sẽ chụp được nhiều tấm ảnh chất lượng ở đây. Đến đây, các bạn có thể trải nghiệm các trò chơi như High wire, Zipline, bắn cung, chinh phục sa mạc cát bằng xe địa hình phân phối lớn... Cắm trại qua đêm trong những lều trại xinh đẹp đầy lãng mạn tại đây sẽ cho bạn những trải nghiệm khó quên. Vườn nho Thái An. Một địa điểm du lịch Ninh Thuận mà bất kỳ du khách nào đến vùng đất này cũng đều muốn ghé thăm chính là các vườn nho. Dường như những bức ảnh được chụp tại các vườn nho đều trở nên ngọt ngào hơn hẳn. Khám phá vườn nho Thái An, ngoài chụp ảnh, các bạn còn được tận tay hái và thưởng thức những chùm nho chín mọng, lúc lỉu trên cành và trải nghiệm các công việc của người dân trồng nho như tham gia cắt tỉa cành, bón phân,… Còn gì tuyệt vời và lãng mạn hơn khi được ngồi thưởng thức bữa trưa bên ly rượu nho thơm nồng, tráng miệng bằng những chùm nho chín mọng ngay tại vườn phải không các cặp đôi! Bãi Tràng. Nếu các bạn muốn có 1 bộ ảnh lãng mạn bên sóng biển ngay tại mãnh đất của gió và cát này thì hãy đến với Bãi Tràng. Bãi Tràng có đường bờ biển vòng cung tuyệt đẹp, cát trắng mịn màng, nước biển trong xanh văn vắt, xung quanh là những mỏm đá núi làm cho khung cảnh nơi đây vừa hoang sơ hùng vĩ, lại vừa nên thơ. Chính sự tĩnh lặng, bao la và khoáng đạt của không gian này sẽ nhanh chóng làm các mê mẩn, nhất là khi được ngắm nhìn bầu trời đêm đầy sao tại nơi đây. Cánh đồng quạt gió. Địa điểm check-in Ninh Thuận tuyệt đẹp cuối cùng trong bài viết này mà các cặp đôi không nên bỏ qua chính là cánh đồng quạt gió. Cảnh ở đây rất đẹp, những tua bin điện gió to cao ngút ngàn nằm giữa những cánh đồng lúa xanh rì bao la bát ngát, xa xa là những đồi núi chập chùng, ngắm thôi đã thấy thích.'),
(6, '5 "Vạn Lý Trường Thành" phiên bản Việt khiến giới trẻ mê đắm', 'van_ly_truong_thanh_vn.jpg', 'Vạn Lý Trường Thành là kỳ quan rất nổi tiếng của Trung Quốc. Đây là nơi ai cũng ao ước được đặt chân đến dù chỉ 1 lần. Tuy nhiên, nếu chưa thể đi đến được với phiên bản gốc này thì các bạn có thể ghé thăm những phiên bản Vạn Lý Trường Thành thu nhỏ theo phiên bản Việt cũng đẹp không kém ngay trong bài viết này. Dù là phiên bản thu nhỏ và không quá đồ sộ như Vạn Lý Trường Thành ở Trung Quốc, nhưng những "Vạn Lý Trường Thành phiên bản Việt" vẫn sở hữu nét đẹp rất riêng không hề thua kém bản gốc thu hút ngàn lượt khách tham quan, chiêm ngưỡng. "Vạn Lý Trường Thành" phiên bản Việt ở Hang Múa, Ninh Bình. Ninh Bình được biết đến là điểm đến tâm linh thu hút nhất khu vực phía Bắc với quần thể chùa Bái Đính đồ sộ và cũng là nơi có "Vạn Lý Trường Thành" phiên bản Việt đẹp nhất ở Việt Nam. Hang Múa nằm dưới chân núi Múa gây ấn tượng với mọi du khách ngay từ ánh nhìn đầu tiên bằng chính con đường dẫn lên đến đỉnh. Để lên đến đỉnh núi, bạn phải chinh phục 468 bậc thang đá uốn lượn, mang màu sắc cổ kính rêu phong cùng khung cảnh hùng vĩ. Nhìn từ xa, con đường này trông hệt như bức tường Thành Vạn Lý. Hai bên bậc thang là những họa tiết trang trí công phu trên đá, với biểu tượng những con rồng, phượng, chim đại bàng được chạm khắc tinh tế, oai phong, đẹp mắt. Trên đỉnh là ngọn tháp sừng sững. Từ đây, chúng ta có thể chiêm ngưỡng được toàn bộ vẻ đẹp của khu vực Tam Cốc với những cánh đồng lúa bạt ngàn, xanh mướt và dòng sông Ngô Đồng quanh co tuyệt đẹp. "Vạn Lý Trường Thành" phiên bản Việt ở Bình Liêu, Quảng Ninh. Bình Liêu được ví như "Vạn Lý Trường Thành thu nhỏ" ở Quảng Ninh và cũng là một trong những "sống lưng khủng long" khó chinh phục nhất ở khu vực miền Bắc khi sở hữu gần 2.000 bậc thang với độ dài 1.8km uốn lượn quanh co qua các vách núi. Trong đó, cung đường lên cột mốc 1035 ở Bình Liêu chính là điểm đến thu hút nhiều phượt thủ nhất trên con đường này. Đến với phiên bản "Vạn Lý Trường Thành" này, bạn sẽ cảm nhận được hết khung cảnh thiên nhiên hoang sơ, với những đỉnh núi hùng vĩ ẩn hiện trong mây mờ. Mỗi mùa "sống lưng khủng long" Bình Liêu lại mang một vẻ đẹp riêng. Đó là khung cảnh những thửa ruộng bậc thang đều tăm tắp mang sắc xanh tươi mát hoặc vàng óng ả; khi thì quyến rũ trong màu cỏ lau, màu của trăm hoa đua nở,… "Vạn Lý Trường Thành" phiên bản Việt ở đồi Mộng Mơ, Đà Lạt. Nằm trong khu du lịch Đồi Mộng Mơ, mô hình "Vạn Lý Trường Thành" phiên bản Việt này đươc dựng nên bởi các tảng đá nguyên khối giống với Vạn Lý Trường Thành của người Trung Quốc. Với tổng chiều dài là 2km uốn khúc qua sườn đồi thoai thoải, dạo bước trên công trình này bạn sẽ thấy những dong chữ "Bất đáo trường thành phi hảo hán" được khắc trên các phiến đá như muốn tái hiện lại một kỳ quan của nhân loại. Công trình này còn khiến nhiều người thích thú khi có các hầm chui tạo cảm giác bí ẩn. "Vạn Lý Trường Thành" phiên bản Việt ở núi Phai Vệ, Lạng Sơn. Núi Phai Vệ ở Lạng Sơn giống như một hòn non bộ khổng lồ giữa thành phố với điểm nhấn là 4 tuyến đường lên xuống tới 535 bậc xây đá cực kỳ kiên cố đến cột cờ Phai Vệ cao 80m. Đây là niềm từ hào của người dân xứ Lạng. Những bậc đá gối kề lên nhau, uốn lượn dẫn lên đỉnh núi, nổi bật trên nền xanh của lan can được đắp mỹ nghệ giả thân cây tre vừa độc đáo. Trông từ xa, con đường này như một "Vạn Lý Trường Thành thu nhỏ". Về đêm, nơi này được gần 1000 bóng đèn led chiếu sáng càng trở nên lung linh huyền ảo. "Vạn Lý Trường Thành" phiên bản Việt ở Khu di tích lịch sử Tức Dụp, An Giang. Từng là một địa điểm kháng chiến nhưng giờ đây, Tức Dụp khoác lên mình một màu xanh của thiên nhiên ban tặng. Đồi Tức Dụp còn gây ấn tượng với du khách bởi con đường ngoằn ngoèo, uốn lượn dài 3km. Con đường này được hội xê dịch gọi tên "Vạn Lý Trường Thành phiên bản miền Tây". Mặc dù không hùng vĩ bằng những "Vạn Lý Trường Thành" bên trên nhưng nơi đây cũng không kém phần thu hút bởi cảnh quan thiên nhiên trong lành, thoáng đãng và những trò chơi vận động hấp dẫn.'),
(7, 'Gợi ý 5 địa điểm du lịch cuối tuần lý tưởng', 'du_lich_cuoi_tuan.jpg', '2 ngày cuối tuần bạn nên đi đổi gió, để nạp lại năng lượng mới với các địa điểm du lịch gần nơi mình đang sống. Ngay từ bây giờ, hãy cùng người thương hoặc nhóm bạn "cạ cứng" của mình lên lịch, xách ba lô lên và đến những địa điểm du lịch cuối tuần lý tưởng này nhé. Làng cổ Đường Lâm, Hà Nội. Làng cổ Đường Lâm giữ cho riêng mình những đặc trưng cơ bản của một ngôi làng xưa với "cây đa, bến nước, sân đình". Đến với Làng cổ Đường Lâm bạn hoàn toàn có thể bị hớp hồn không chỉ những ngôi nhà với lối kiến trúc cổ mà còn bởi sự niềm nở của con người nơi đây. Cái cảm giác được men theo những con đường làng quanh co uốn lượn, ngồi quán nước đầu làng ngắm khung cảnh của những cánh đồng lúa xanh um, len qua từng ngõ ngách ngắm từng kiến trúc ngôi nhà cổ, chỉ một ngày ở Đường Lâm cũng đủ để làm nên những dư vị tuyệt vời. Cuối tuần này, bạn có thể rủ rê hội cạ cứng của mình từ bây giờ, lên kế hoạch cho chuyến đi trong ngày khám phá khu làng cổ tuyệt đẹp với những góc sống ảo xưa cũ hoàn hảo, tận hưởng khung cảnh làng quê yên bình, trải nghiệm cuộc sống dân dã ngay giữa lòng thủ đô hiện đại. Bãi Đông, Thanh Hóa. Không tấp nập, không xô bồ như những bãi tắm nổi tiếng khác, khung cảnh bãi Đông yên bình, nhẹ nhàng, đem tới không gian thư giãn khá lý tưởng. Bãi Đông còn khá hoang sơ với vẻ đẹp tự nhiên của bãi cát trắng trải dài, nước biển xanh tron, đoạn đường đá vắng vẻ. Những ghềnh đá, bãi đá lớn nhỏ nằm bên cạnh bãi Đông là một địa điểm "sống ảo" thu hút được nhiều bạn trẻ. Thung lũng Tình Yêu, Đà Lạt. Thung lũng Tình Yêu là địa danh nổi tiếng bậc nhất tại xứ sở sương mù Đà Lạt. Với những thảm cỏ xanh và những đồi thông chập chùng bao quanh thung lũng, đây là nơi hẹn hò lý tưởng của những đôi tình nhân. Các bạn có thể men theo những lối mòn hoặc leo cả trăm bật cấp, đi qua những cổng hoa với màu sắc rực rỡ để lên đồi Vọng Cảnh. Từ đây Thung lũng Tình yêu hiện ra trong tầm mắt đẹp tựa như một bức tranh, sinh động với những cánh buồm chấp chới trên hồ. Nhờ có hồ mà thung lũng này càng thêm thơ mộng. Những con đường đất đỏ uốn lượn vòng vèo có thể đưa khách lên đồi hoặc dẫn đền tận đỉnh núi Langbiang thấp thoáng trong mây. Cần Giờ, Tp Hồ Chí Minh. Cần Giờ là điểm đến cuối tuần gần Sài Gòn lý tưởng để thưởng thức hải sản và dạo biển. Nơi đây vừa có biển, vừa có rừng nên chúng ta có thể bỏ lại bộn bề cuộc sống, hoà mình vào thiên nhiên mây trời. Ngoài những món hải sản tươi "roi rói" thì Cần Giờ còn có khu du lịch rừng ngập mặn Vàm Sát - khu bảo tồn và dự trữ sinh quyển lớn nhất Việt Nam; khu du lịch sinh thái biển Phương Nam; đảo Khỉ; biển 30/4; chợ hải sản Hàng Dương; đảo Thạnh An… cho các bạn thỏa sức khám phá. Khu du lịch Đồng Tháp Mười, Long An. Khu du lịch Đồng Tháp Mười là khu sinh thái đặc trưng của Long An nói riêng và của Nam Bộ nói chung, không chỉ hấp dẫn du khách bởi khu rừng tràm rộng lớn mà còn khiến mọi người ngỡ ngàng và mê mẩn bởi những cánh đồng sen ngút ngàn. Khám phá khu sinh thái Đồng Tháp Mười, bạn còn có cơ hội đến tham quan khu bảo tồn đất ngập nước Láng Sen với gần 112 loài chim, 149 loài động vật, trong đó có nhiều loài quý hiếm như cò, sếu đầu đỏ, rùa, trăn đấy, rắn ráo,... Không chỉ dừng lại ở đó, khu bảo tồn này còn phô diễn vẻ đẹp của mình trước mắt du khách với thảm thực vật ven sông trù phú, những đồng cỏ ngập nước theo mùa, những đầm lầy giàu sức sống là ngôi nhà chung của quần thể động thực vật đang cư trú tại nơi đây.');
